// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

interface IERC20 {
    function balanceOf(address) external view returns (uint256);
    function transfer(address to, uint256 amount) external returns (bool);
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function approve(address spender, uint256 amount) external returns (bool);
}

interface IERC20Permit {
    function permit(address owner, address spender, uint256 value, uint256 deadline, uint8 v, bytes32 r, bytes32 s) external;
}

contract SimpleCalibur {
    event DepositedWithPull(address indexed user, address indexed token, address indexed vault, uint256 amount, bytes vaultReturnData);
    event DepositedWithPermit(address indexed user, address indexed token, address indexed vault, uint256 amount, bytes vaultReturnData);
    event DepositedWithPermitBytes(address indexed user, address indexed token, address indexed vault, uint256 amount, bytes vaultReturnData);

    function depositWithPull(
        address token,
        address vault,
        uint256 amount,
        bytes calldata depositCalldata
    ) external payable {
        require(amount > 0, "amount=0");
        bool ok = IERC20(token).transferFrom(msg.sender, address(this), amount);
        require(ok, "transferFrom failed");
        ok = IERC20(token).approve(vault, amount);
        require(ok, "approve failed");
        (bool success, bytes memory ret) = vault.call{value: 0}(depositCalldata);
        require(success, getRevertMsg(ret));
        uint256 leftover = IERC20(token).balanceOf(address(this));
        if (leftover > 0) {
            IERC20(token).transfer(msg.sender, leftover);
        }
        emit DepositedWithPull(msg.sender, token, vault, amount, ret);
    }

    function depositWithPermit(
        address token,
        address vault,
        uint256 amount,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s,
        bytes calldata depositCalldata
    ) external payable {
        require(amount > 0, "amount=0");
        IERC20Permit(token).permit(msg.sender, address(this), amount, deadline, v, r, s);
        bool ok = IERC20(token).transferFrom(msg.sender, address(this), amount);
        require(ok, "transferFrom failed");
        ok = IERC20(token).approve(vault, amount);
        require(ok, "approve failed");
        (bool success, bytes memory ret) = vault.call{value: 0}(depositCalldata);
        require(success, getRevertMsg(ret));
        uint256 leftover = IERC20(token).balanceOf(address(this));
        if (leftover > 0) {
            IERC20(token).transfer(msg.sender, leftover);
        }
        emit DepositedWithPermit(msg.sender, token, vault, amount, ret);
    }

    /// @notice Generic single-tx flow: caller supplies `permitCalldata` bytes (ABI call) that will be executed on `token` (or Permit2 contract)
    /// permitCalldata typically is ABI for token.permit(...) or Permit2.permit(...). If empty, no permit call executed.
    function depositWithPermitBytes(
        address token,
        address vault,
        uint256 amount,
        bytes calldata permitCalldata,
        bytes calldata depositCalldata
    ) external payable {
        require(amount > 0, "amount=0");
        if (permitCalldata.length > 0) {
            (bool okPerm, bytes memory retPerm) = token.call(permitCalldata);
            require(okPerm, getRevertMsg(retPerm));
        }
        bool ok = IERC20(token).transferFrom(msg.sender, address(this), amount);
        require(ok, "transferFrom failed");
        ok = IERC20(token).approve(vault, amount);
        require(ok, "approve failed");
        (bool success, bytes memory ret) = vault.call{value: 0}(depositCalldata);
        require(success, getRevertMsg(ret));
        uint256 leftover = IERC20(token).balanceOf(address(this));
        if (leftover > 0) {
            IERC20(token).transfer(msg.sender, leftover);
        }
        emit DepositedWithPermitBytes(msg.sender, token, vault, amount, ret);
    }

    function getRevertMsg(bytes memory _returnData) internal pure returns (string memory) {
        if (_returnData.length < 68) return "vault call reverted";
        assembly {
            _returnData := add(_returnData, 0x04)
        }
        return abi.decode(_returnData, (string)); 
    }

    receive() external payable {}
}
